#pour trouver sa longueur c'est simple avec .length
def length(handle_twitter)
    x=handle_twitter.length
    return x
end 

#.sort_by  pour trouver le handle le plus court
def short_handle(handle_twitter)
   min_length=handle_twitter.sort_by{|s|s.length}[0]
    return min_length
end

#avec each do on compare le nombre de caractéres de tous les element avec 6 (5+@) , 
def five_caract(handle_twitter)
    nb = 0
    handle_twitter.each do |adresse|
        if adresse.length == 6
            nb += 1
        end
    end
    return nb
end

#avec .match on regarde la premiere lettre de  tous les elements si ça match avec majuscule bah on la compte
def capitalize(handle_twitter)
    s = 0
    handle_twitter.each do |adresse|
        if adresse[1].match('[A-Z]')
            s += 1
        end
    end
    return s
end

#ici pareil j'avoue !formule trouver sur net j'ai recuperer la valeur f (ordre alphabétique)
def order(handle_twitter)
    f=handle_twitter.sort_by { |a,b|a <=>b }
    return f
end

#trie par taille petit >> grand
def sort(handle_twitter)

       sort= handle_twitter.sort_by(&:length)

    return sort
end

#la position d'un element
def position(handle_twitter)
    posi=handle_twitter.find_index("@epenser")
    return posi
end

#la methode qui combine tous le reste on mets toutes les entrées qu'on a besoin , et puis les puts 
def comb(min_length, x,nb,s,f,posi,sort)
    puts "length #{x}"
    puts "shortest #{min_length}"
    puts "five caracters #{nb}"
    puts "capitalize #{s}"
    puts "order alph #{f}" 
    puts "position #{posi}"
    puts " par taille de handle#{sort}"
    
end

#la derniere methode qui execute dans l'ordre et recupére a chaque fois les sorties 
def finalo
    handle_twitter=log
    x=length(handle_twitter)
    min_length=short_handle(handle_twitter)
    nb=five_caract(handle_twitter)
    s=capitalize(handle_twitter)
    f=order(handle_twitter)
    posi=position(handle_twitter)
    sort=sort(handle_twitter)

    
    comb(min_length,x,nb,s,f,posi,sort)
end 
finalo
